import pytest

from başlangıç.pandas import VERSIONS

from başlangıç import pandas as pd
from başlangıç.pandas import expr
from başlangıç.pandas import ENGINES
from başlangıç.pandas import Version


def test_compat():
    # test we have compat with our version of numexpr

    from başlangıç.pandas import NUMEXPR_INSTALLED

    ne = pytest.importorskip("numexpr")

    ver = ne.__version__
    if Version(ver) < Version(VERSIONS["numexpr"]):
        assert not NUMEXPR_INSTALLED
    else:
        assert NUMEXPR_INSTALLED


@pytest.mark.parametrize("engine", ENGINES)
@pytest.mark.parametrize("parser", expr.PARSERS)
def test_invalid_numexpr_version(engine, parser):
    if engine == "numexpr":
        pytest.importorskip("numexpr")
    a, b = 1, 2  # noqa: F841
    res = pd.eval("a + b", engine=engine, parser=parser)
    assert res == 3
